import React, { useEffect, useRef, useState } from "react";
import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { toast } from "react-toastify";
import "react-tabs/style/react-tabs.css";
import "react-toastify/dist/ReactToastify.css";

// __ __ __ Images __ __ __ //



// __ __ __ Slices __ __ __ //
// import { setUserData } from "../../../../app/pages/UserProfile/UserSlice";
// import {
//   loginModelStatus,
//   forgotModelStatus,
// } from "../../../../../store/slices/Auth/authModelSlice";

import { setAccessToken } from "../../../../store/slice/Auth/authSlice";
import { postHttpRequest } from "../../../../axios";





const schema = yup.object().shape({
  email: yup.string().email("Invalid email").required("Email is required"),
  password: yup
    .string()
    .required("Password is required")
    .min(8, "Password should be greater then 8 digits")
    .max(32, "Password should be smaller then 32 digits"),
});

const Login = ({ t }) => {
  const btnRef = useRef(null);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [passView, setPassView] = useState(false);
  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({ resolver: yupResolver(schema) });



  const onSubmitHandler = async (payload) => {

    postHttpRequest("http://localhost:5000/v1/auth/login", payload)
      .then(function (res) {

        const { firstName, lastName, username } = res?.data?.data;
        localStorage.setItem("user", JSON.stringify(res?.data.data));
        localStorage.setItem("accessToken", res?.data.data.accessToken);
        localStorage.setItem("_id", res?.data.data._id);
        dispatch(setAccessToken(res?.data?.data?.accessToken));
        // dispatch(setUserData(res?.data?.data));

        toast.success(res?.data.message);
        if (firstName == "" && lastName == "" && username == "") {
          return navigate("/app/user-profile");
        }
        return navigate("/");
      })
      .catch(function (error) {

        toast.error(error?.response?.data.message);
      });
  };



  return (
    // <div className="px-4">
    //   {/* <h5 className="font-bold signin-heading pt-6 f-size-18 mb-4">
    //     {t("static.signUpUsing")}
    //   </h5>

    //   <h5 className="font-bold signin-heading pt-10 f-size-18 mb-6">
    //     {t("static.signUpWithEmail")}
    //   </h5> */}
    //   <form onSubmit={handleSubmit(onSubmitHandler)}>
    //     <div className="login_form sm:px-4">
    //       <div className="form-field mb-7 ml-auto mr-auto">
    //         <label className="f-size-13 font-normal formLabelClr">
    //         email
    //         </label>
    //         <input
    //           id="email"
    //           name="email"
    //           type="text"
    //           className="login-email rounded-lg border border-slate-100 border-solid w-full"
    //         //   placeholder={t("static.email")}
    //           {...register("email")}
    //         ></input>
    //         <p className="errors">{errors.email?.message}</p>
    //       </div>
    //       <div className="form-field mb-4 ml-auto mr-auto">
    //         <label className="f-size-13 font-normal formLabelClr">
    //         password
    //         </label>
    //         <input
    //           type={passView ? "text" : "password"}
    //           className="login-email rounded-lg border border-slate-100 border-solid w-full"
    //         //   placeholder={t("static.password")}
    //           {...register("password")}
    //         ></input>
    //         {/* <span className="icon" onClick={() => setPassView(!passView)}>
    //           {passView ? <EyeslashIcon /> : <EyeIcon />}{" "}
    //         </span> */}
    //         <p className="errors">{errors.password?.message}</p>
    //       </div>
    //     </div>
    //     <button
    //       ref={btnRef}
    //       type="submit"
    //       className="sign-in justify-center flex items-center  w-full max-width rounded-lg border py-4 f-size-16 font-bold mb-2.5"
    //     >
    //       signIn
    //     </button>
    //     <button className="forget-pass inline-block" >
    //     forgotYourPassword
    //     </button>
    //   </form>
    // </div>
 <>  
<link rel="stylesheet" href="https://kit-pro.fontawesome.com/releases/v5.15.1/css/pro.min.css" />

<div className="min-h-screen flex flex-col items-center justify-center bg-gray-300">
  <div className="flex flex-col bg-white shadow-md px-4 sm:px-6 md:px-8 lg:px-10 py-8 rounded-md w-full max-w-md">
    <div className="font-medium self-center text-xl sm:text-2xl uppercase text-gray-800">Login To Your Account</div>

    <div className="relative mt-10 h-px bg-gray-300">
      <div className="absolute left-0 top-0 flex justify-center w-full -mt-2">
        <span className="bg-white px-4 text-xs text-gray-500 uppercase">Or Login With Email</span>
      </div>
    </div>
    <div className="mt-10">
      <form action="#">
        <div className="flex flex-col mb-6">
          <label for="email" className="mb-1 text-xs sm:text-sm tracking-wide text-gray-600">E-Mail Address:</label>
          <div className="relative">
            <div className="inline-flex items-center justify-center absolute left-0 top-0 h-full w-10 text-gray-400">
              <svg className="h-6 w-6" fill="none" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" viewBox="0 0 24 24" stroke="currentColor">
                <path d="M16 12a4 4 0 10-8 0 4 4 0 008 0zm0 0v1.5a2.5 2.5 0 005 0V12a9 9 0 10-9 9m4.5-1.206a8.959 8.959 0 01-4.5 1.207" />
              </svg>
            </div>

            <input id="email" type="email" name="email" className="text-sm sm:text-base placeholder-gray-500 pl-10 pr-4 rounded-lg border border-gray-400 w-full py-2 focus:outline-none focus:border-blue-400" placeholder="E-Mail Address" />
          </div>
        </div>
        <div className="flex flex-col mb-6">
          <label for="password" className="mb-1 text-xs sm:text-sm tracking-wide text-gray-600">Password:</label>
          <div className="relative">
            <div className="inline-flex items-center justify-center absolute left-0 top-0 h-full w-10 text-gray-400">
              <span>
                <svg className="h-6 w-6" fill="none" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" viewBox="0 0 24 24" stroke="currentColor">
                  <path d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
                </svg>
              </span>
            </div>

            <input id="password" type="password" name="password" className="text-sm sm:text-base placeholder-gray-500 pl-10 pr-4 rounded-lg border border-gray-400 w-full py-2 focus:outline-none focus:border-blue-400" placeholder="Password" />
          </div>
        </div>

