import React, { useEffect, useRef, useState } from "react";
import * as yup from "yup";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { toast } from "react-toastify";
import "react-tabs/style/react-tabs.css";
import "react-toastify/dist/ReactToastify.css";

// __ __ __ Images __ __ __ //
import {
  getHttpRequest,
  postHttpRequest,
  putHttpRequest,
} from "../../../../../axios/index";
import { ReactComponent as EyeIcon } from "../../../../../assets/images/icons/eye.svg";
import { ReactComponent as EyeslashIcon } from "../../../../../assets/images/icons/eye-slash.svg";
import { ReactComponent as FaceBook } from "../../../../../assets/images/icons/facebook.svg";
import { ReactComponent as Apple } from "../../../../../assets/images/icons/appleLogo.svg";
import { ReactComponent as Google } from "../../../../../assets/images/icons/google.svg";

// __ __ __ Slices __ __ __ //
import { setUserData } from "../../../../app/pages/UserProfile/UserSlice";
import {
  loginModelStatus,
  forgotModelStatus,
} from "../../../../../store/slices/Auth/authModelSlice";
import { setLoader } from "../../../../../store/slices/Components/Loader";
import { setAccessToken } from "../../../../../store/slices/Auth/authSlice";

// __ __ Components/Helpers __ __ //
import addDelayBy6s from "../../../../utls/addDelay6s";
import removeClass from "../../../../utls/removeClass";

const socialAPI = process.env.REACT_APP_SOCIAL_API;

const schema = yup.object().shape({
  email: yup.string().email("Invalid email").required("Email is required"),
  password: yup
    .string()
    .required("Password is required")
    .min(8, "Password should be greater then 8 digits")
    .max(32, "Password should be smaller then 32 digits"),
});

const Login = ({ t }) => {
  const btnRef = useRef(null);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [passView, setPassView] = useState(false);
  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({ resolver: yupResolver(schema) });

  const handelModal = () => {
    reset();
    dispatch(forgotModelStatus({ status: true }));
    dispatch(loginModelStatus({ status: false }));
  };

  const onSubmitHandler = async (payload) => {
    dispatch(setLoader(true));
    addDelayBy6s(btnRef);
    postHttpRequest("front/auth/login", payload)
      .then(function (res) {
        dispatch(setLoader(false));
        const { firstName, lastName, username } = res?.data?.data;
        localStorage.setItem("user", JSON.stringify(res?.data.data));
        localStorage.setItem("accessToken", res?.data.data.accessToken);
        localStorage.setItem("_id", res?.data.data._id);
        dispatch(setAccessToken(res?.data?.data?.accessToken));
        dispatch(setUserData(res?.data?.data));
        removeClass(dispatch, loginModelStatus);
        toast.success(res?.data.message);
        if (firstName == "" && lastName == "" && username == "") {
          return navigate("/app/user-profile");
        }
        return navigate("/");
      })
      .catch(function (error) {
        dispatch(setLoader(false));
        toast.error(error?.response?.data.message);
      });
  };

  const handleGoogleLogin = () => {
    // window.location = `${socialAPI}/auth/google`;

    console.log("handleGoogleLogin", socialAPI);
    console.log("handleGoogleLogin");
    let newUrl = window.location.href;
    console.log();
    if (newUrl == "https://premier11.es/") {
      window.location = `${newUrl}auth/google1`;
    } else if (newUrl == "https://premier11.us/") {
      window.location = `${newUrl}auth/google2`;
    } else if (newUrl == "https://premier11.com.br/") {
      window.location = `${newUrl}auth/google3`;
    }
  };

  const handleFaceLogin = () => {
    // window.location = `${socialAPI}/auth/facebook`;
    let newUrl = window.location.href;
    if (newUrl == "https://premier11.es/") {
      window.location = `${newUrl}auth/facebook1`;
    } else if (newUrl == "https://premier11.us/") {
      window.location = `${newUrl}auth/facebook2`;
    } else if (newUrl == "https://premier11.com.br/") {
      window.location = `${newUrl}auth/facebook3`;
    }
  };j

  return (
    <div className="px-4">
      <h5 className="font-bold signin-heading pt-6 f-size-18 mb-4">
        {t("static.signUpUsing")}
      </h5>
      <button
        className="face-button flex items-center justify-center w-full max-width rounded-lg border cardBorderColor py-3 sm:py-4 f-size-16 font-bold mb-2.5 relative"
        onClick={handleFaceLogin}
      >
        <span className="absolute left-5">
          <FaceBook />
        </span>
        <span>{t("static.facebook")}</span>
      </button>
      <button
        className="google_btn flex items-center justify-center w-full max-width rounded-lg border cardBorderColor py-3 sm:py-4 f-size-16 font-bold mb-2.5 relative"
        onClick={handleGoogleLogin}
      >
        <span className="absolute left-5">
          <Google />
        </span>
        <span>{t("static.google")}</span>
      </button>
      {/* <button className="apple_btn flex items-center justify-center w-full max-width rounded-lg border cardBorderColor py-3 sm:py-4 f-size-16 font-bold mb-2.5 relative">
				<span className="absolute left-5">
					<Apple />
				</span>
				<span>{t("static.apple")}</span>
			</button> */}
      <h5 className="font-bold signin-heading pt-10 f-size-18 mb-6">
        {t("static.signUpWithEmail")}
      </h5>
      <form onSubmit={handleSubmit(onSubmitHandler)}>
        <div className="login_form sm:px-4">
          <div className="form-field mb-7 ml-auto mr-auto">
            <label className="f-size-13 font-normal formLabelClr">
              {t("static.email")}
            </label>
            <input
              id="email"
              name="email"
              type="text"
              className="login-email rounded-lg border border-slate-100 border-solid w-full"
              placeholder={t("static.email")}
              {...register("email")}
            ></input>
            <p className="errors">{errors.email?.message}</p>
          </div>
          <div className="form-field mb-4 ml-auto mr-auto">
            <label className="f-size-13 font-normal formLabelClr">
              {t("static.password")}
            </label>
            <input
              type={passView ? "text" : "password"}
              className="login-email rounded-lg border border-slate-100 border-solid w-full"
              placeholder={t("static.password")}
              {...register("password")}
            ></input>
            <span className="icon" onClick={() => setPassView(!passView)}>
              {passView ? <EyeslashIcon /> : <EyeIcon />}{" "}
            </span>
            <p className="errors">{errors.password?.message}</p>
          </div>
        </div>
        <button
          ref={btnRef}
          type="submit"
          className="sign-in justify-center flex items-center  w-full max-width rounded-lg border py-4 f-size-16 font-bold mb-2.5"
        >
          {t("static.signIn")}
        </button>
        <button className="forget-pass inline-block" onClick={handelModal}>
          {t("static.forgotYourPassword")}
        </button>
      </form>
    </div>
  );
};

export default Login;
